mapper.select:
SELECT f.*, c.name as category_name, u.real_name as user_name FROM found_item f LEFT JOIN category c ON f.category_id = c.id LEFT JOIN user u ON f.user_id = u.id
AND (f.title LIKE CONCAT('%', #{params.keyword}, '%') OR f.description LIKE CONCAT('%', #{params.keyword}, '%'))
AND f.category_id = #{params.categoryId}
AND f.status = #{params.status}
AND f.found_location LIKE CONCAT('%', #{params.location}, '%')
ORDER BY f.created_at DESC
LIMIT #{params.offset}, #{params.limit}

SELECT COUNT(*) FROM found_item f


        AND (f.title LIKE CONCAT('%', #{params.keyword}, '%') 
        OR f.description LIKE CONCAT('%', #{params.keyword}, '%'))


        AND f.category_id = #{params.categoryId}


        AND f.status = #{params.status}


        AND f.found_location LIKE CONCAT('%', #{params.location}, '%')
