mapper.select:
SELECT l.*, c.name as category_name, u.real_name as user_name FROM lost_item l LEFT JOIN category c ON l.category_id = c.id LEFT JOIN user u ON l.user_id = u.id
AND (l.title LIKE CONCAT('%', #{params.keyword}, '%') OR l.description LIKE CONCAT('%', #{params.keyword}, '%'))
AND l.category_id = #{params.categoryId}
AND l.status = #{params.status}
AND l.lost_location LIKE CONCAT('%', #{params.location}, '%')
ORDER BY l.created_at DESC
LIMIT #{params.offset}, #{params.limit}

SELECT COUNT(*) FROM lost_item l


        AND (l.title LIKE CONCAT('%', #{params.keyword}, '%') 
        OR l.description LIKE CONCAT('%', #{params.keyword}, '%'))


        AND l.category_id = #{params.categoryId}


        AND l.status = #{params.status}


        AND l.lost_location LIKE CONCAT('%', #{params.location}, '%')
