mapper.update:
UPDATE pet_info
name = #{name}, type = #{type}, breed = #{breed}, age = #{age}, gender = #{gender}, status = #{status}, description = #{description}, image_url = #{imageUrl}, health_status = #{healthStatus}, vaccine_status = #{vaccineStatus}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT * FROM pet_info


        AND type = #{type}


        AND status = #{status}


ORDER BY create_time DESC
