mapper.select:
SELECT * FROM `order`
WHERE deleted = 0
AND customer_id = #{customerId}
AND status = #{status}
ORDER BY created_at DESC

mapper.update:
UPDATE `order`
package_id = #{packageId}, photographer_id = #{photographerId}, appointment_id = #{appointmentId}, total_amount = #{totalAmount}, deposit_amount = #{depositAmount}, balance_amount = #{balanceAmount}, paid_amount = #{paidAmount}, status = #{status}, payment_method = #{paymentMethod}, shooting_date = #{shootingDate}, photo_selection_date = #{photoSelectionDate}, pickup_date = #{pickupDate}, remark = #{remark}, deleted = #{deleted},
WHERE id = #{id}
