mapper.select:
SELECT m.*, mc.name AS category_name
FROM movie m LEFT JOIN movie_category mc ON m.category_id = mc.id
WHERE m.id = #{id}

mapper.update:
UPDATE movie
title = #{title}, poster = #{poster}, director = #{director}, actors = #{actors}, category_id = #{categoryId}, duration = #{duration}, release_date = #{releaseDate}, description = #{description}, status = #{status},
WHERE id = #{id}
