mapper.select:
SELECT b.*, s.name AS station_name FROM bike b LEFT JOIN station s ON b.station_id = s.id


        AND b.bike_no LIKE CONCAT('%', #{bikeNo}, '%')


        AND b.type = #{type}


        AND b.status = #{status}


        AND b.station_id = #{stationId}


ORDER BY b.create_time DESC

mapper.update:
UPDATE bike
bike_no = #{bikeNo}, type = #{type}, status = #{status}, station_id = #{stationId}, battery_level = #{batteryLevel},
WHERE id = #{id}
