mapper.select:
SELECT l.*, e.name as employee_name, a.name as approver_name
FROM leave_request l
LEFT JOIN employee e ON l.employee_id = e.id
LEFT JOIN user a ON l.approver_id = a.id


        AND l.employee_id = #{employeeId}


        AND e.name LIKE CONCAT('%', #{employeeName}, '%')


        AND l.type = #{type}


        AND l.status = #{status}


ORDER BY l.create_time DESC

mapper.update:
UPDATE leave_request
type = #{type}, start_date = #{startDate}, end_date = #{endDate}, days = #{days}, reason = #{reason}, status = #{status}, approver_id = #{approverId}, approve_time = #{approveTime},
WHERE id = #{id}
