mapper.select:
SELECT r.*, p.name as position_name, d.name as department_name
FROM recruitment r
LEFT JOIN position p ON r.position_id = p.id
LEFT JOIN department d ON r.department_id = d.id


        AND r.position_id = #{positionId}


        AND r.department_id = #{departmentId}


        AND r.status = #{status}


ORDER BY r.create_time DESC

mapper.update:
UPDATE recruitment
position_id = #{positionId}, department_id = #{departmentId}, count = #{count}, salary_range = #{salaryRange}, requirements = #{requirements}, status = #{status},
WHERE id = #{id}
