mapper.select:
SELECT r.*, p.name as position_name FROM resume r LEFT JOIN recruitment rec ON r.recruitment_id = rec.id LEFT JOIN position p ON rec.position_id = p.id
AND r.recruitment_id = #{recruitmentId}
AND r.name LIKE CONCAT('%', #{name}, '%')
AND r.status = #{status}
ORDER BY r.create_time DESC

mapper.update:
UPDATE resume
recruitment_id = #{recruitmentId}, name = #{name}, phone = #{phone}, email = #{email}, education = #{education}, experience = #{experience}, status = #{status},
WHERE id = #{id}
