mapper.select:
SELECT s.*, e.name as employee_name, e.emp_no
FROM salary s
LEFT JOIN employee e ON s.employee_id = e.id


        AND s.employee_id = #{employeeId}


        AND e.name LIKE CONCAT('%', #{employeeName}, '%')


        AND s.year_month = #{yearMonth}


        AND s.status = #{status}


ORDER BY s.year_month DESC, s.create_time DESC

mapper.update:
UPDATE salary
year_month = #{yearMonth}, base_salary = #{baseSalary}, performance = #{performance}, allowance = #{allowance}, deduction = #{deduction}, actual_salary = #{actualSalary}, status = #{status}, pay_date = #{payDate},
WHERE id = #{id}
